import Typical from "react-typical";
import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

import { toast } from "react-toastify";

import imgBack from "../../../src/images/mailz.jpeg";
import load1 from "../../../src/images/load2.gif";

import ScreenHeading from "../../Utilities/ScreenHeading/ScreenHeading";
import ScrollService from "../../Utilities/ScrollService";
import Animations from "../../Utilities/Animations";
import Footer from "../Footer/Footer";
import "./ContactMe.css";

export default function ContactMe(props) {
  let fadeInScreenHandler = (screen) => {
      if (screen.fadeInScreen !== props.id) return;
      Animations.animations.fadeInScreen(props.id);
    };
  
    const fadeInSubscription =
      ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

  const form = useRef();
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_n9dn4g8', 'template_gqxls3c', form.current, 'H19pJ94wWpFCOz1eZ')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      alert("Email has been sent")
  };

  return (
    <div className="main-container" id={props.id || ""}>
      <ScreenHeading title={"Contact Me"} subHeading={"Lets Keep In Touch"} />
      <div className="central-form">
        <div className="col">
          <h2 className="title">
            <Typical
              loop={Infinity}
              steps={[
                "Get In Touch üëç",
                2000,
                "Text ‚úàÔ∏è",
                2000,
                "Text üòä",
                2000,
                "Text üñ•Ô∏è",
                2000,
              ]}
            />
          </h2>{""}
          <a href="#">
            <i className="fa fa-facebook-square"></i>
          </a>
          <a href="#">
            <i className="fa fa-google-plus-square"></i>
          </a>
          <a href="#">
            <i className="fa fa-instagram"></i>
          </a>
          <a href="#">
            <i className="fa fa-youtube-square"></i>
          </a>
          <a href="#">
            <i className="fa fa-twiter"></i>
          </a>
        </div>
        <div className="back-form">
          <div className="img-back">
            <h4>Send Your Email Here</h4>
            <img src={imgBack} alt="image not found" />
          </div>
          <form ref={form} onSubmit={sendEmail}>
      

            <label htmlFor="name">Name</label>
            <input type="text" name="user_name" re  />

            <label htmlFor="email">Email</label>
            <input type="email" name="user_email"   />

            <label htmlFor="mobile">Mobile No</label>
            <input type="number" name="contact_no" />

            <label htmlFor="message">Message</label>
            <textarea type="text" name="message" />

            <div className="send-btn">
              <button type="submit">
                Send
                <i className="fa fa-paper-plane" />
              </button> 
            </div>
          </form>
        </div>
      </div>
      <Footer/>
    </div>
     
  );
}
